mapper.select:
SELECT US.RANK_NUM AS NUM, S.SEASON_NAME AS NAME, S.SEASON_END_TIME AS TIME FROM TB_USER U JOIN TB_USER_SEASON US ON U.USER_NUM = US.USER_NUM JOIN TB_SEASON S ON US.SEASON_NUM = S.SEASON_NUM WHERE U.USER_ID = #{userId} AND S.SEASON_NUM < (SELECT MAX(SEASON_NUM) FROM TB_SEASON)
UNION
SELECT B.BADGE_NUM AS NUM, B.BADGE_NAME AS NAME, A.ACQUISITION_TIME AS TIME FROM TB_USER U JOIN TB_ACHIEVEMENT A ON U.USER_NUM = A.USER_NUM JOIN TB_BADGE B ON A.BADGE_NUM = B.BADGE_NUM WHERE U.USER_ID = #{userId}
ORDER BY TIME DESC;
